import './App.css';
import { useState, useEffect } from 'react';
import { Routes, Route } from 'react-router-dom';
import axios from 'axios';
import Passkey from './component/passkey.js';
import Board from './component/Board';
import ProtectedRoute from './component/ProtectedRoute';
import LandingPage from './component/LandingPage.js';

function App() {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [isLoading, setIsLoading] = useState(true); // Add loading state
  
  // Check for stored token on app load
  useEffect(() => {
    const checkToken = async () => {
      const token = localStorage.getItem('authToken');
      console.log('Checking for stored token:', token ? 'Found' : 'Not found');
      
      if (token) {
        try {
          // Verify the token is still valid
          const response = await axios.post('http://localhost:5200/webauthn/verify-token', { token });
          console.log('Token verification response:', response.data);
          if (response.data.success) {
            console.log('Token is valid, setting authenticated to true');
            setIsAuthenticated(true);
          } else {
            // Token is invalid, remove it
            console.log('Token verification failed, removing token');
            localStorage.removeItem('authToken');
          }
        } catch (error) {
          console.error('Token verification failed:', error);
          localStorage.removeItem('authToken');
        }
      }
      setIsLoading(false);
    };
    
    checkToken();
  }, []);
  
  // Logout function to clear token and authentication state
  const handleLogout = () => {
    localStorage.removeItem('authToken');
    setIsAuthenticated(false);
  };
  
  if (isLoading) {
    return <div>Loading...</div>; // Show loading while checking token
  }
  
  return (
    <Routes>
      <Route path="/" element={<LandingPage />} />
      <Route path="/login" element={<Passkey setIsAuthenticated={setIsAuthenticated} />} />
      
      <Route
        path="/tictactoe"
        element={
          <ProtectedRoute
            element={<Board />}
            isAuthenticated={isAuthenticated}
          />
        }
      />
    </Routes>
  );
}

export default App;
